onSubmit() {
  const idPerson = this.inforUser.idPerson;

    // Aquí puedes llamar a un método del servicio REST para enviar los datos del formulario al servidor
  this.api.putUser(this.inforUser).subscribe(data =>{ 
    console.log(idPerson);
  });
}


/** Permite guardar un usuario */
postform(form:PersonPruebaI)
{
  console.log(form);
