import { Component, OnInit } from '@angular/core';
import {Router, ActivatedRoute} from '@angular/router'
import { PersonI } from 'src/app/models/person.interface';
import {ApiEvertecService} from '../../servicios/api/api-evertec.service';
import {FormGroup, FormControl, Validators} from '@angular/forms';
import { DatePipe } from '@angular/common';
import {AlertsService} from '../../servicios/alerts/alerts.service';


@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})


export class EditComponent implements OnInit {

  constructor(private activerouter:ActivatedRoute, private router:Router, private api:ApiEvertecService, private alert:AlertsService){

  }
  // En el componente, puedes crear un objeto de tipo PersonPruebaI con valores por defecto para todas las propiedades requeridas:
 inforUser: PersonI = {
  idPerson: 0,
  name: '',
  lastName: '',
  birthdate: new Date(),
  photo: '',
  idMaritalStatus: 0,
  haveBrothers: false
};

haveBrothers:string = "false";
birthdate:string = "1990-01-01";

ngOnInit(): void{
  let userId:number= Number(this.activerouter.snapshot.paramMap.get('id'));
  this.getUser(userId);  
}


/** Consulta un usuario por id */
getUser(userId: number)
{
this.api.getUser(userId).subscribe(data =>{   
    this.inforUser= data;
   const datePipe = new DatePipe('en-CO');
   this.birthdate = datePipe.transform(this.inforUser.birthdate, 'shortDate')!;
   this.haveBrothers = this.inforUser.haveBrothers? "true": "false";

   console.log(this.inforUser);

    // Inicializar el objeto person con el valor del idPerson
   //this.inforUser.idPerson = this.inforUser.idPerson;
   /* this.editForm.setValue({
      'idPerson': this.inforUser.idPerson,
      /*'name': this.inforUser.name,
      'lastName': this.inforUser.lastName,
      'birthdate': datePipe.transform(this.inforUser.birthdate, 'dd/MM/yyyy'),
      'photo':    this.inforUser.photo,
      'idMaritalStatus':this.inforUser.idMaritalStatus,
      'haveBrothers': this.inforUser.haveBrothers
    })*/
  })
}

//editar un usuario
editUser() {
  this.inforUser.haveBrothers = this.parseBoolean(this.haveBrothers);
  console.log(this.inforUser);

  //editar usuario
  this.api.putUser(this.inforUser).subscribe(data =>{ 

    /*if(data.status === 200)
      this.alert.showSuccess("Usuario modificado exitosamente","Edición Exitosa");
    else
      this.alert.showError("Se presentó un errr al editar el usuario","Error");*/
      console.log(data);
      this.alert.showSuccess("Usuario modificado exitosamente","Edición Exitosa");
    this.router.navigate(['user']);
  });
}

//Convierte un string a boolean
private parseBoolean(value: string): boolean {
  return value === 'true';
}

}
<app-header></app-header>

<div class="container">
    <form (ngSubmit)="editUser()" #myForm="ngForm">
    <div class="row row-centered form-group">
        <div class="col-lg-4 col-md-4 col-sm-12 col-xs-12 col-centered">
            <!--<label>ID Usuario</label>
            <input type="number" class="form-control" formControlName="idPerson" required/>-->
            <label for="idPerson">ID Person:</label>
            <input type="text" id="idPerson" name="idPerson" [(ngModel)]="inforUser.idPerson" requerid [disabled]="true">
        </div> 

        <!-- <div class="col-lg-4 col-md-4 col-sm-12 col-xs-12 col-centered">

         </div>
         <div class="col-lg-4 col-md-4 col-sm-12 col-xs-12 col-centered">
            <label>Foto</label>             
             <input type="file" id="photo" class="form-control" formControlName="photo">
             <img [src]="editForm.get('photo').value" *ngIf="editForm.get('photo').value">
         </div>-->
    </div>

        <div class="row row-centered form-group">
            <div class="col-lg-6 col-md-6 col-sm-12 col-xs-12 col-centered">
                <label>Nombre</label>
                <input type="text" class="form-control" name="name" [(ngModel)]="inforUser.name" required>

            </div>
           <div class="col-lg-6 col-md-6 col-sm-12 col-xs-12 col-centered">
                <label>Apellido</label>
                <input type="text" class="form-control" name="lastname" [(ngModel)]="inforUser.lastName" required>
            </div>
        </div>   
        <div class="row row-centered form-group">
            <div class="col-lg-3 col-md-3 col-sm-6 col-xs-6 col-centered">
                <label>Fecha de Nacimiento: {{inforUser.birthdate | date:'dd/MM/yyyy'}}</label>
                <input type="date" class="form-control" name="birthdate" [(ngModel)]="inforUser.birthdate" required >
            </div>
            <div class="col-lg-3 col-md-3 col-sm-6 col-xs-6 col-centered">
                <label>Estado Civil</label><br/>
                <select id="idMaritalStatus"  name="idMaritalStatus" [(ngModel)]="inforUser.idMaritalStatus" >
                    <option value="1">Soltero</option>
                    <option value="2">Casado</option>
                </select>
            </div>
            <div class="col-lg-3 col-md-3 col-sm-3 col-xs-3 col-centered">

                <label>Tiene Hijos</label>
                <br/>
                <label>
                    <input type="radio" name="haveBrothers" value='true' [(ngModel)]="haveBrothers"  required>
                    Sí
                  </label>
                  &nbsp;
                  <label>
                    <input type="radio" name="haveBrothers" value='false' [(ngModel)]="haveBrothers"  required>
                    No
                  </label>
            </div>
        </div>

        <div>        
            <button type="submit" class="btn btn-primary">Guardar</button>
        </div>

    </form>

</div>


<app-footer></app-footer>
