import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'yesNo'
})
export class YesNoPipe implements PipeTransform {
  transform(value: string): string {
    const boolValue = value.toLowerCase() === 'true';
    return boolValue ? 'Si' : 'No';
  }
}
