import { Component, OnInit} from '@angular/core';
import {ApiEvertecService} from '../../servicios/api-evertec.service'
import { PersonI } from 'src/app/models/person.interface';

import { Observable, tap, catchError } from 'rxjs';
import { HttpClient } from '@angular/common/http';


@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.css']
})


export class UserComponent implements OnInit{

  myobserver$!: Observable<Array<PersonI>>;
//constructor(private api:ApiEvertecService)
constructor(private http: HttpClient){
  //this.getPeople();
}

/*getPeople(): void{
   this.api.getUsers().subscribe(data => {
    console.log(data)
  });
}*/

getPeople():Observable<any>{
  const url = 'http://localhost:56403/api/People';
  return this.http.get<Array<PersonI>>(url).pipe(
    tap(_ => console.log('fetched hero')),
    catchError(async (error) => console.error(error))
  );
}

ngOnInit(): void {
  this.myobserver$ = this.getPeople();
  console.log(this.getPeople().subscribe(data=> console.log(data)));
}


}
